using GetirClone.Domain.Entities;
using Microsoft.EntityFrameworkCore;
using Microsoft.EntityFrameworkCore.Metadata.Builders;

namespace GetirClone.Persistance.Configurations
{
    public class CardTypeConfiguration : IEntityTypeConfiguration<CardType>
    {
        public void Configure(EntityTypeBuilder<CardType> builder)
        {

            builder.HasData(new List<CardType>
            {
                new ()
                {
                    Id=1,
                    Description="MasterCard",
                    RegexPattern="^(?:5[1-5][0-9]{2}|222[1-9]|22[3-9][0-9]|2[3-6][0-9]{2}|27[01][0-9]|2720)[0-9]{12}$",
                    CardTypeImageUrl = "data:image/svg+xml;base64,"
                },
                new ()
                {
                    Id=2,
                    Description="Visa",
                    RegexPattern="^4[0-9]{12}(?:[0-9]{3})?$",
                    CardTypeImageUrl="data:image/svg+xml;base64,"
                }
            });
        }
    }
}
